import { Component, OnInit} from '@angular/core';
import { BookService } from '../book.service';
import { Book } from '../book.model';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit {
  books: Book[] = [];
  private subscription!: Subscription;  
  constructor(private bookService: BookService) {}

  ngOnInit(): void {
    this.subscription = this.bookService.books$.subscribe((data: Book[]) => {  
      this.books = data;
    });
    this.bookService.getBooks();
  }

}
<div class="book-container">
  <div *ngFor="let book of books" class="book">
    <h2>Szerző: {{ book.author }}</h2>
    <h3>Cím: {{ book.title }}</h3>
    <h3>Kiadás éve: {{ book.year }}</h3>
    <h4>Ár: {{ book.cost }} HUF</h4>
    <h4>Értékelés: {{ book.rating }}</h4>
  </div>
</div>
